import React, {useContext} from "react";

import ScrollTip from "components/ScrollTip";
import PageLink from 'components/PageLink';
import {CurrentThemeContext} from 'contexts/currentTheme';

import styles from './AppFallbackComponent.module.scss'


const AppFallbackComponent = () => {
    const [currentThemeState] = useContext(CurrentThemeContext);
    const text = currentThemeState.isLang === 'ru' ? 'В РАЗРАБОТКЕ' : 'UNDER CONSTRUCTION';
    return (
        <div className={`${styles.appFallbackComponentWrapper} ${currentThemeState.isLight ? styles.light : styles.dark}`}
        style={{paddingTop: 130}}>
            <ScrollTip/>
            <div className={styles.appFallbackComponent}>
                <div className={styles.appFallbackComponentButton}>
                   <span className={styles.spanWrapper}>
                       <span>
                           {text}
                       </span>
                   </span>
                </div>
            </div>
            <PageLink text='MAIN' url='/' />
        </div>
    )
};

export default AppFallbackComponent;
