import React, {useContext, useState} from "react";
import {Link} from "react-router-dom";
import ReactPageScroller from 'react-page-scroller';

import ScrollTip from "components/ScrollTip";
import subArray from 'utilities/subArray.js';

import styles from './Contacts.module.scss'
import {CurrentThemeContext} from "contexts/currentTheme";


const Contacts = () => {
    const [currentThemeState] = useContext(CurrentThemeContext);
    const [currentPage, setCurrentPage] = useState(null);
    const contactItem = ['worker-1', "worker-2", "worker-3", "worker-4", "worker-5", "worker-6", 'worker-7', "worker-8", "worker-9", "worker-10", "worker-11", "worker-12"];
    const data = subArray(contactItem, 6);

    const handlePageChange = (number) => {
        setCurrentPage(number)
    };

    return (
        <div className={`${styles.contactsWrapper} ${currentThemeState.isLight ? styles.light : styles.dark}`}>
            <ScrollTip currentPage={currentPage} length={data.length}/>
            <ReactPageScroller
                containerWidth='100%'
                containerHeight='100%'
                transitionTimingFunction='ease-in-out'
                pageOnChange={handlePageChange}
                customPageNumber={currentPage}
                blockScrollDown={currentPage === data.length - 1}
            >
                {data.map((item, idx) => {
                    return (
                        <div className={styles.contacts} key={`item-${idx}`}>
                            {item.map((person, idx) => {
                                return (
                                    <div className={styles.personWrapper} key={`${person}-${idx}`}>
                                        <div className={styles.personHeader}>
                                            <div className={styles.personPhoto}>

                                            </div>
                                            <div className={styles.personText}>
                                                Lorem ipsum dolor sit amet, consectetur adipisicing elit. At
                                                consectetur, debitis dolores eius exercitationem ipsa iusto libero minus
                                                non numquam quae quos rem, repellat tempore vitae? Dolor doloremque
                                                dolorum qui!
                                            </div>
                                        </div>
                                        <div className={styles.personFooter}>
                                            <Link to={`/our_team/${person}`} className={styles.personButton}>

                                            </Link>
                                            <div className={styles.personPosition}>
                                                <div className={styles.personalPositionItem}>
                                                    CEO
                                                </div>
                                                <div className={styles.personalPositionItem}>
                                                    OWNER
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                )
                            })}
                        </div>
                    )
                })}

            </ReactPageScroller>
        </div>
    )
};


export default Contacts;