import React, {useState, useContext} from "react";
import ReactPageScroller from 'react-page-scroller';
import {Link} from 'react-router-dom'

import styles from './PortfolioSingle.module.scss'
import {CurrentThemeContext} from "contexts/currentTheme";
import ScrollTip from "components/ScrollTip";
import PageLink from "components/PageLink";
import ButtonFreeez from "components/ButtonFreeez";
import Modal from "components/Modal";

const PortfolioSingle = () => {
    const [currentThemeState, dispatch] = useContext(CurrentThemeContext);
    const [currentPage, setCurrentPage] = useState(null);
    const nameItem = 'HEINEKEN';

    const handlePageChange = (number) => {
        setCurrentPage(number)
    };

    const handleModalChange = () => {
        dispatch({type: 'SET_MODAL', payload: true});
    };

    return (
        <div className={`${styles.portfolioSingleWrapper} ${currentThemeState.isLight ? styles.light : styles.dark}`}>
            <ScrollTip currentPage={currentPage} length={2}/>
            <div className={styles.nameItemWrapper}>
                <div className={styles.nameItem}>
                    {nameItem}
                </div>
            </div>
            <div className={styles.portfolioSingle}>
                <ReactPageScroller
                    containerWidth='100%'
                    containerHeight={`calc(100% - 36px)`}
                    transitionTimingFunction='ease-in-out'
                    pageOnChange={handlePageChange}
                >
                    <PortfolioSingleImage handleModalChange={handleModalChange}/>
                    <PortfolioSingleText/>
                </ReactPageScroller>
            </div>
            <div className={styles.portfolioSingleButton}>
                <ButtonFreeez text='PREVIEW' close='Cross' theme='light' width='300px' align='left' url='/portfolio'/>
                <ButtonFreeez text='ADD TO SHOPPING BAG' close='Plus' theme='dark' width='300px' align='left'/>
            </div>
            <PageLink text='BACK' url='/portfolio'/>
            <Modal />
        </div>
    )
};

