import React, {useContext} from "react";

import {CurrentThemeContext} from 'contexts/currentTheme';

import styles from './ScrollTip.module.scss'

const ScrollTip = ({currentPage, length, theme}) => {
    const [currentThemeState] = useContext(CurrentThemeContext);
    const scroll_up = currentThemeState.isLang === 'ru' ? 'СКРОЛЛ ВВЕРХ' : 'SCROLL UP';
    const scroll_down = currentThemeState.isLang === 'ru' ? 'СКРОЛЛ ВНИЗ' : 'SCROLL DOWN';
    let themeScroll = theme ? theme : 'dark';
    if (currentThemeState.isLight) {
        themeScroll = themeScroll === 'dark' ? 'light' : 'dark'
    }


    const dataLength = currentPage+1;
    let vizScrollUp = 0;
    let vizScrollDown = 0;
    if(dataLength === 1 && dataLength < length){
        vizScrollUp = 0;
        vizScrollDown = 1;
    }
    if(dataLength < length && length > 1 && dataLength !== length && dataLength !== 1){
        vizScrollUp = 1;
        vizScrollDown = 1;
    }
    if(dataLength === length && length > 1){
        vizScrollUp = 1;
        vizScrollDown = 0;
    }

    return (
        <div className={`${styles.scrollTip} ${themeScroll === 'light' ? styles.scrollTipLight : styles.scrollTipDark}`}>
            <div className={styles.scrollDown} style={{opacity: vizScrollDown}}>
                <div className={styles.scrollDownOval}>
                    <div className={styles.scrollDownOvalLine}> </div>
                </div>
                <div className={styles.scrollDownLineWrapper}>
                    <div className={styles.scrollDownEmpty}> </div>
                    <div className={styles.scrollDownLine}> </div>
                    <div className={styles.scrollDownText}>{scroll_down}</div>
                </div>
            </div>
            <div className={styles.scrollUp} style={{opacity: vizScrollUp}}>
                <div className={styles.scrollUpLineWrapper}>
                    <div className={styles.scrollUpEmpty}> </div>
                    <div className={styles.scrollUpLine}> </div>
                    <div className={styles.scrollUpText}>{scroll_up}</div>
                </div>
                <div className={styles.scrollUpOval}>
                    <div className={styles.scrollUpOvalLine}> </div>
                </div>
            </div>
        </div>
    )
};
export default ScrollTip
