import React, {useEffect} from "react";

import useFetch from 'hooks/useFetch';

const SectionName = ({id}) => {
    const apiUrl = `/wp-json/wp/v2/categories/${id}`
    const [{response}, doFetch] = useFetch(apiUrl)
    let name = ''

    useEffect(() => {
        doFetch()
    }, [doFetch])

    if (response) {

       name = response.name
    }
    return (
        <React.Fragment>
            {name}
        </React.Fragment>

    )
}
export default SectionName
