import React from "react";

import {Select} from 'antd';

import './SelectFreeez.scss'

const {Option} = Select;
const SelectFreeez = ({data, placeholder, width}) => {
    const onChange = (value) => {
        console.log(`selected ${value}`);
    };

    const onBlur = () => {
        console.log('blur');
    };

    const onFocus = () => {
        console.log('focus');
    };

    const onSearch = (val) => {
        console.log('search:', val);
    };

    return (
        <Select
            className='SelectFreeez'
            showSearch
            style={{width: width}}
            placeholder={placeholder}
            optionFilterProp="children"
            onChange={onChange}
            onFocus={onFocus}
            onBlur={onBlur}
            onSearch={onSearch}
            filterOption={(input, option) =>
                option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
            }
        >

            {data.map((item,idx)=>(
                <Option key={`${item}-${idx}`} value={item}>{item}</Option>
            ))}
        </Select>
    )
};
export default SelectFreeez


