import React, {useState} from "react";

import styles from './ServicesItem.module.scss'

const ServicesItem = ({text}) => {
    const dark = false;
    const [isActive, setActive] = useState(false);
    const handleClick = () => {
        setActive(!isActive)
    };
    return (
        <button className={`${dark ? styles.servicesItemDark : styles.servicesItemLight} ${isActive ? styles.active : ''}`} onClick={handleClick}>
            {text}
            <span className={styles.servicesItemArrow}>
               <svg width="17" height="20" viewBox="0 0 17 20" fill="none" xmlns="http://www.w3.org/2000/svg">
<path d="M9.37981 0H0L7.62018 10L0 20H9.37981L17 10L9.37981 0Z" fill={dark ? '#232027' : '#EFEEF3'} fill-opacity="0.25" className={styles.firstArrow}/>
</svg>
                <svg width="17" height="20" viewBox="0 0 17 20" fill="none" xmlns="http://www.w3.org/2000/svg">
<path d="M9.37981 0H0L7.62018 10L0 20H9.37981L17 10L9.37981 0Z" fill={dark ? '#232027' : '#EFEEF3'} fill-opacity="0.35" className={styles.secondArrow}/>
</svg>
                <svg width="17" height="20" viewBox="0 0 17 20" fill="none" xmlns="http://www.w3.org/2000/svg">
<path d="M9.37981 0H0L7.62018 10L0 20H9.37981L17 10L9.37981 0Z" fill={dark ? '#232027' : '#EFEEF3'} fill-opacity="0.45" className={styles.thirdArrow}/>
</svg>
            </span>
            <span className={styles.servicesItemCross}>
                <span> </span>
                <span> </span>
            </span>
        </button>
    )
}
export default ServicesItem