import React, {createContext, useReducer} from 'react'

const initialState = {
    expressBag:[],
    trainingBag:[]
}

const reducer = (state, action) => {
    switch (action.type) {
        case 'SET_TRAINING_BAG':
            return {
                ...state,
                trainingBag: action.payload
            }
        case 'SET_EXPRESS_BAG':
            return {
                ...state,
                expressBag: action.payload
            }
        default:
            return state
    }
}

export const CurrentShoppingBagContext = createContext()

export const CurrentShoppingBagProvider = ({children}) => {
    const value = useReducer(reducer, initialState)

    return (
        <CurrentShoppingBagContext.Provider value={value}>
            {children}
        </CurrentShoppingBagContext.Provider>
    )
}
