import React from "react"
import {Switch, Route} from 'react-router-dom'

import MainPage from 'Pages/MainPage'
import Portfolio from 'Pages/Portfolio'
import PortfolioSingle from 'Pages/PortfolioSingle'
import Services from 'Pages/Services'
import Contacts from "Pages/Contacts";
import AllContacts from "Pages/AllContacts";
import PersonalContacts from "Pages/PersonalContacts";
import AboutFreez from "Pages/AboutFreeez";
import ExpressDesign from "Pages/ExpressDesign";
import ExperimentalGameDesign from "Pages/ExperimentalGameDesign";
import Authentication from "Pages/Authentication";
import Registration from "Pages/Registration";
import Cart from "Pages/Cart";
import AppFallbackComponent from "Pages/AppFallbackComponent";
import SingleContacts from "Pages/SingleContacts";
import ServicesSingle from "Pages/ServicesSingle";

export default () => {
    return (
        <Switch>
            <Route path='/' component={MainPage} exact />
            <Route path='/portfolio' component={Portfolio} exact />
            <Route path='/portfolio/:slag' component={PortfolioSingle} />
            <Route path='/services' component={Services} exact />
            <Route path='/services/:slag' component={ServicesSingle} />
            <Route path='/our_team' component={Contacts} exact />
            <Route path='/our_team/:slag' component={SingleContacts} />
            <Route path='/contacts' component={AllContacts} />
            <Route path='/personal_contacts' component={PersonalContacts} />
            <Route path='/about_freez' component={AboutFreez} />
            <Route path='/express_design' component={ExpressDesign} />
            <Route path='/experimental_game_design' component={ExperimentalGameDesign} />
            <Route path='/authentication' component={Authentication} />
            <Route path='/register' component={Registration} />
            <Route path='/cart' component={Cart} />
            <Route path='*' exact component={AppFallbackComponent} />
        </Switch>
    )
}
